pragma solidity ^0.4.20;
import "./Store.sol";

library SafeMath {
  function mul(uint256 a, uint256 b) internal pure returns (uint256) {
    uint256 c = a * b;
    assert(a == 0 || c / a == b);
    return c;
  }

  function div(uint256 a, uint256 b) internal  pure returns (uint256) {
    uint256 c = a / b;
    return c;
  }

  function sub(uint256 a, uint256 b) internal  pure returns (uint256) {
    assert(b <= a);
    return a - b;
  }

  function add(uint256 a, uint256 b) internal pure  returns (uint256) {
    uint256 c = a + b;
    assert(c >= a);
    return c;
  }
}

contract Product {
    using SafeMath for uint;

    address public owner;
    address public storeAddress;
    Store public store;

    string public name;
    string public description;
    uint public price;

    bool public approved = false;

    uint public quantity;
    uint public numberOfSales = 0;
    uint public numberOfSuccess = 0;
    uint public numberOfRatings = 0;
    uint public ratingSum = 0;
    uint public ratingAverage = 0;
    uint public numberOfDisputes = 0;
    uint public numberOfResolvedDisputes = 0;

    mapping(uint => bool) public pendingList;
    mapping(uint => bool) public disputesList;

    struct Sale {
        address buyer;
        uint price;
        uint quantity;
        bool success;
        bool dispute;
        bool resolved;
        uint8 rating;
        string review;
        string reason;
    }
    mapping(uint => Sale) public sales;

    modifier onlyOwner(uint _saleID) {
        require(sales[_saleID].buyer == msg.sender);
        _;
    }
    modifier onlyPending(uint _saleID) {
        require(pendingList[_saleID] == true);
        _;
    }
    modifier approvedByStore() {
        require(approved == true);
        _;
    }

    event Sold(
        address saleBuyer,
        uint saleKey
    );

    constructor(address _store, string _name, string _description, uint _price,
        uint _quantity) public {
        store = Store(_store);
        storeAddress = _store;
        // require(Store.owner() == msg.sender);
        owner = msg.sender;
        name = _name;
        description = _description;
        price = _price;
        quantity = _quantity;
    }

    function buy(uint _price, uint _quantity) public approvedByStore payable returns(uint)  {
        // Price must equal price and _quantity > quantity
        require(price == _price);
        require(quantity >= _quantity);
        require(msg.value == _price*_quantity);
        quantity = quantity.sub(_quantity);

        numberOfSales = numberOfSales.add(1);

        sales[numberOfSales] = Sale(
            msg.sender,
            _price,
            _quantity,
            false,
            false,
            false,
            0,
            '',
            ''
            );
        pendingList[numberOfSales] = true;
        owner.transfer(msg.value);
        //
        store.newSale();
        emit Sold(msg.sender,numberOfSales);
        return numberOfSales;
    }

    function submitSuccess(uint _saleID, uint8 _rating, string _review) onlyOwner(_saleID) onlyPending(_saleID) public returns(bool) {
        sales[_saleID].success = true;
        sales[_saleID].dispute = false;
        sales[_saleID].review = _review;
        sales[_saleID].rating = _rating;
        pendingList[_saleID] = false;
        numberOfSuccess = numberOfSuccess.add(1);
        numberOfRatings = numberOfRatings.add(1);
        ratingSum = ratingSum.add(_rating);
        ratingAverage = ratingSum.div(numberOfRatings);
        //
        store.newSuccess(_rating);
        return true;
    }
    function submitDispute(uint _saleID, string _reason) onlyOwner(_saleID) onlyPending(_saleID) public returns(bool) {
        sales[_saleID].success = false;
        sales[_saleID].dispute = true;
        sales[_saleID].reason = _reason;
        pendingList[_saleID] = false;
        disputesList[_saleID] = true;
        numberOfDisputes = numberOfDisputes.add(1);
        //
        store.newDispute();
        return true;
    }
    function resolveDispute(uint _saleID, uint8 _rating, string _review) onlyOwner(_saleID) public returns(bool) {
        require(sales[_saleID].dispute == true);
        sales[_saleID].resolved = true;
        sales[_saleID].review = _review;
        sales[_saleID].rating = _rating;
        numberOfResolvedDisputes = numberOfResolvedDisputes.add(1);
        numberOfRatings = numberOfRatings.add(1);
        ratingSum = ratingSum.add(_rating);
        ratingAverage = ratingSum.div(numberOfRatings);
        //
        store.newResolvedDispute(_rating);
        return true;
    }

    // Store owner functions
    function changeQuantity(uint _quantity) public returns(uint) {
        require(msg.sender == owner);
        quantity = _quantity;
        return quantity;
    }
    function approve() public {
        require(msg.sender == storeAddress);
        approved = true;
    }


}
