pragma solidity ^0.4.20;
import "./Product.sol";
/* import "./SafeMath.sol"; */

contract Store {
    using SafeMath for uint;

    address public owner;
    string public name;
    // address public symbol;
    uint public numberOfSales = 0;
    uint public numberOfSuccess = 0;
    uint public numberOfRatings = 0;
    uint public numberOfDisputes = 0;
    uint public numberOfResolvedDisputes = 0;

    uint public ratingSum = 0;

    /* Product public ProductContract; */

    mapping(address => bool) public productList;

    struct ProductStruct {
        uint numberOfSales;
        uint numberOfSuccess;
        uint numberOfRatings;
        uint numberOfDisputes;
        uint numberOfResolvedDisputes;
        uint ratingSum;
        //uint ratingAverage;
    }
    mapping(address => ProductStruct) public products;

    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    modifier onlyProduct() {
        require(productList[msg.sender] == true);
        _;
    }

    constructor(string _name) public {
        name = _name;
        owner = msg.sender;
    }
    function newProduct(address _productAddress) public onlyOwner {
        productList[_productAddress] = true;
        Product pc = Product(_productAddress);
        pc.approve();
    }
    function newSale() public onlyProduct {
        products[msg.sender].numberOfSales += 1;
        numberOfSales += 1;
    }
    function newSuccess(uint _rating) public onlyProduct {
        products[msg.sender].numberOfSuccess += 1;
        products[msg.sender].ratingSum += _rating;
        products[msg.sender].numberOfRatings += 1;
        ratingSum += _rating;
        numberOfRatings += 1;
        numberOfSuccess += 1;
    }
    function newDispute() public onlyProduct {
        products[msg.sender].numberOfDisputes += 1;
        numberOfDisputes += 1;
    }
    function newResolvedDispute(uint _rating) public onlyProduct {
        products[msg.sender].numberOfResolvedDisputes += 1;
        products[msg.sender].ratingSum += _rating;
        products[msg.sender].numberOfRatings += 1;
        ratingSum += _rating;
        numberOfRatings += 1;
        numberOfResolvedDisputes += 1;
    }

    function productRatingAverage(address _productAddress) public view returns(uint) {
        return products[_productAddress].ratingSum.div(products[_productAddress].numberOfRatings);
    }
    function storeRatingAverage() public view returns(uint) {
        return ratingSum.div(numberOfRatings);
    }




}
